function i42nApply(n){n=void 0!==n?n:navigator.language||navigator.userLanguage,document.querySelectorAll("[data-content]").forEach(t=>{t.innerHTML=i42n[String(n)][t.getAttribute("data-content")]})}function i42nInit(n){return function(){document.querySelectorAll(".i42n").forEach(n=>{n.addEventListener("click",function(){i42nApply(n.getAttribute("data-lang"))})}),i42nApply(n)}}